{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"provenance":[],"authorship_tag":"ABX9TyPNn1MAV8JVCUA2NxYNran8"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","execution_count":null,"metadata":{"id":"2qQarH20LAkJ"},"outputs":[],"source":["# 20240820_vizualization_code.R\n","\n","require(ggplot2)\n","require(dplyr)\n","# CODE TO REPRODUCE FIGURES\n","outpath ='/Volumes/fh/fast/gilbert_p/fg_data/t1d_v3/c2hq_edit1_versus_matrix'\n","input_file =file.path('/Volumes/fh/fast/gilbert_p/fg_data/t1d_v3/c2hq_edit1_versus_matrix',\n","                      '20240819_df1hq_hla_ft_class_ii_and_i_conditional_in_cohort_1_T1D_status.csv')\n","d = readr::read_csv(input_file)\n","d = d %>%\n","  mutate(log10odds = log10(odds_est),\n","         log10p = -1*log10(p_exact),\n","         log10q = -1*log10(fdr),\n","         log10qx = -1*log10(fdr_cond)) %>%\n","  mutate(risk_sum_gt = ifelse(risk_sum > .5, 1, 0))\n","\n","dxred  = d %>% filter(fdr <.2, log10odds > 0)\n","dxblue  = d %>% filter(fdr <.2, log10odds <0 )\n","# Primary Figure 1\n","gg1 = ggplot(d, aes(x = log10odds, y= log10p)) +\n","  geom_point(size = .1) +\n","  geom_point(data = dxblue, col = 'blue' , pch= 1 , alpha = .55)+\n","  geom_point(data = dxred ,col = 'red' , pch= 1 , alpha = .55)+\n","  theme_classic() +\n","  geom_vline(aes(xintercept = 0), linetype = \"dashed\") +\n","  ylab(expression(-log[10]~'(p)')) +\n","  xlab(expression(log[10]~'(odds ratio)'))\n","\n","pdf(file.path(outpath, 'FIG1_20240820_df1hq_hla_ft_class_ii_and_i_conditional_in_cohort_1_T1D_status.primary.pdf'), width = 4, height = 4)\n","gg1\n","dev.off()\n","\n","dx = bind_rows(dxred, dxblue)\n","dx\n","gg2 = dx %>% mutate(direction=sign(log10odds))%>%\n","  group_by(cond, direction) %>%\n","  summarise(n = n()) %>%\n","  mutate(value = direction*n)%>%\n","  ggplot(aes(y = cond, x= value)) +\n","  geom_point(aes(col = factor(direction)), pch = 1) +\n","  theme_bw() +\n","  geom_vline(aes(xintercept = 0), linetype = \"dashed\") +\n","  ylab(\"\")+\n","  xlab(\"\") +\n","  scale_color_manual(values = c(\"blue\",\"red\")) +\n","  theme(legend.position = \"none\") +\n","  scale_x_continuous(breaks = c(-40, -30, -20, -10, 0, 10), labels = c( c(40, 30, 20, 10, 0, 10))) +\n","  coord_cartesian(xlim = c(-40,10))\n","\n","pdf(file.path(outpath, 'FIG2_20240820_df1hq_hla_ft_class_ii_and_i_conditional_in_cohort_1_T1D_status.primary.pdf'),\n","    width = 4, height = 4)\n","gg2\n","dev.off()\n","\n","\n","gg1s = ggplot(d, aes(x = log10odds, y= log10p)) +\n","  geom_point(size = .1) +\n","  geom_point(data = dxblue, col = 'blue' , pch= 1 , alpha = .55)+\n","  geom_point(data = dxred ,col = 'red' , pch= 1 , alpha = .55)+\n","  theme_classic() +\n","  facet_wrap(~cond,ncol = 7) +\n","  geom_vline(aes(xintercept = 0), linetype = \"dashed\") +\n","  ylab(expression(-log[10]~'(p)')) +\n","  xlab(expression(log[10]~'(odds ratio)')) +\n","  theme(strip.text =element_text(size = 6))\n","\n","pdf(file.path(outpath, '/FIG1SUP_20240620_df1hq_hla_ft_class_ii_and_i_conditional_in_cohort_1_T1D_status.primary.pdf'),\n","    width = 8, height = 11)\n","gg1s\n","dev.off()\n","\n","\n","\n","require(ggplot2)\n","require(dplyr)\n","\n","# LOAD FROM SUPPLIMENTAL FILE - 20240412_sequences_for_CDR3_risk_analysis.xlsx\n","d\n","\n","# FUNCTION TO CONVERT TO ALLELE FORMAT  DRB1_0301 --> \"DRB1*03:01\"\n","convert_allele_format <- function(allele_string) {\n","  # Split the string on \"__\" if it indicates combined alleles\n","  allele_parts <- unlist(strsplit(allele_string, \"__\"))\n","\n","  # Function to convert single allele part\n","  convert_part <- function(part) {\n","    # Replace underscore with asterisk\n","    part <- gsub(\"_\", \"*\", part, fixed = TRUE)\n","    # Insert colon before the last two digits\n","    sub(\"([0-9]{2})([0-9]{2})$\", \"\\\\1:\\\\2\", part)\n","  }\n","\n","  # Apply the conversion function to each part\n","  converted_parts <- sapply(allele_parts, convert_part)\n","\n","  # Combine parts back into a single string if there are multiple parts\n","  if (length(converted_parts) > 1) {\n","    return(paste(converted_parts, collapse = \" \"))\n","  } else {\n","    return(converted_parts)\n","  }\n","}\n","print(convert_allele_format(\"DQA1_0101__DQB1_0201\"))\n","print(convert_allele_format(\"DRB1_0301\"))\n","\n","d1i= d %>% filter(stringr::str_starts(string = binary, pattern =\"[ABC]\"))\n","d1ii=d %>% filter(!stringr::str_starts(string = binary, pattern =\"[ABC]\"))\n","\n","# ASSEMBLE PLOT DATA\n","a= d1ii %>%   mutate(allele = substr(binary, 1, 2)) %>%\n","  group_by(binary, allele) %>%\n","  tally() %>%\n","  mutate(type = 'edit 1') %>%\n","  filter(!stringr::str_starts(string = binary, pattern = \"DRB[345]\")) %>%\n","  mutate(binary = convert_allele_format(binary))\n","b = d0ii %>%\n","  mutate(allele = substr(binary, 1, 2)) %>%\n","  group_by(binary, allele) %>%\n","  tally() %>%\n","  mutate(type = 'exact') %>%\n","  filter(!stringr::str_starts(string = binary, pattern = \"DRB[345]\")) %>%\n","  mutate(binary = convert_allele_format(binary))\n","\n","# CLASS 2 DATA\n","data2 = bind_rows(a)#,b)\n","g2 = ggplot(data2,aes(x = n, y = forcats::fct_reorder(binary,n), fill = type)) +\n","  geom_point(shape = 21) +\n","  scale_x_log10() +\n","  theme_bw()+\n","  theme(legend.position = \"bottom\")+\n","  scale_fill_manual(\"\",values = c(\"black\",'gray')) +\n","  ylab(\"\") + xlab(\"\") +\n","  facet_grid(allele~\"MHC Class II Associated TCRs\", scales = \"free_y\",space = \"free_y\")+\n","  theme(strip.background = element_rect(fill = \"white\",color = \"white\"),\n","        strip.text.y = element_text(angle = 0, size = 8),\n","        strip.text.x = element_text(angle = 0, size = 8),\n","        axis.text = element_text(size = 8)) +\n","  theme(panel.grid.minor= element_blank()) +\n","  theme(panel.grid.major.x = element_blank()) + theme(legend.position = \"none\")\n","\n","g2\n","\n","d1i %>% group_by(binary) %>% tally() %>% mutate(type = 'd1i')\n","d0i %>% group_by(binary) %>% tally() %>% mutate(type = 'd0i')\n","a1= d1i %>%   mutate(allele = substr(binary, 1, 1)) %>%\n","  group_by(binary, allele) %>%\n","  tally() %>%\n","  mutate(type = 'edit 1') %>%\n","  filter(!stringr::str_starts(string = binary, pattern = \"DRB[345]\")) %>%\n","  mutate(binary = convert_allele_format(binary))\n","b1 = d0i %>%\n","  mutate(allele = substr(binary, 1, 1)) %>%\n","  group_by(binary, allele) %>%\n","  tally() %>%\n","  mutate(type = 'exact') %>%\n","  filter(!stringr::str_starts(string = binary, pattern = \"DRB[345]\")) %>%\n","  mutate(binary = convert_allele_format(binary))\n","\n","# CLASS 1 DATA\n","data1 = bind_rows(a1)#b1)\n","g1 = ggplot(data1,aes(x = n, y = forcats::fct_reorder(binary,n), fill = type)) +\n","  geom_point(shape = 21) +\n","  scale_x_log10() +\n","  theme_bw()+\n","  theme(legend.position = \"bottom\")+\n","  scale_fill_manual(\"\",values = c(\"black\",'gray')) +\n","  ylab(\"\") + xlab(\"\") +\n","  facet_grid(allele~\"MHC Class I Associated TCRs\", scales = \"free_y\",space = \"free_y\") +\n","  theme(strip.background = element_rect(fill = \"white\",color = \"white\"),\n","        strip.text.y = element_text(angle = 0, size = 8),\n","        strip.text.x = element_text(angle = 0, size = 8),\n","        axis.text = element_text(size = 8)) +\n","  theme(panel.grid.minor= element_blank()) +\n","  theme(panel.grid.major.x = element_blank()) + theme(legend.position = \"none\")\n","g2\n","\n","pdf(file.path(outpath, 'FIG3SUP_20240620_df1hq_hla_ft_class_ii_and_i_conditional_in_cohort_1_number_of_associated_TCRs_by_allele.pdf'),\n","    height = 8, width = 6.5)\n","gridExtra::grid.arrange(g1,g2, ncol = 2, layout_matrix= matrix(c(1,1,1,2,2,2,2), nrow = 1))\n","dev.off()\n","\n"]}]}